@import "./base.scss";
.coolLink {
  position: relative;
  display: grid;
  place-items: center;
  overflow: hidden;
  &::before {
    content: "";
    position: relative;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 3px;
    background: currentColor;
    transform: scale3d(0, 1, 1);
    transform-origin: right;
    transition: transform 0.5s cubic-bezier(0.5, 0.5, 0.3, 1);
  }
  &::after {
    content: attr(data-text);
    position: absolute;
    top: 10%;
    height: 100%;
    transform: translate3d(150%, 0, 0);
  }
  &:hover::before {
    transform: scale3d(1, 1, 1);
    transform-origin: left;
  }
  &:hover::after {
    transform: translate3d(0, 0, 0);
  }
  & span {
    transition: transform 0.5s cubic-bezier(0.5, 0.5, 0.3, 1);
  }
  &:hover span {
    transform: translate3d(-150%, 0, 0);
  }
}
.coolButton {
  position: relative;
  border: none;
  cursor: pointer;
  width: 175px;
  height: 120px;
  background: none;
  color: chartreuse;
  &::before,
  &::after {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }
  &::before {
    content: "";
    background-color: #fff;
    clip-path: path(
      "M154.5,88.5 C131,113.5 62.5,110 30,89.5 C-2.5,69 -3.5,42 4.5,25.5 C12.5,9 33.5,-6 85,3.5 C136.5,13 178,63.5 154.5,88.5 Z"
    );
    transition: clip-path 0.5s cubic-bezier(0.585, 2.5, 0.645, 0.55);
  }
  &:hover::before {
    clip-path: path(
      "M143,77 C117,96 74,100.5 45.5,91.5 C17,82.5 -10.5,57 5.5,31.5 C21.5,6 79,-5.5 130.5,4 C182,13.5 169,58 143,77 Z"
    );
  }
  & span {
    mix-blend-mode: difference;
    font-weight: 500;
  }
  &::after {
    content: "";
    height: 85%;
    width: 95%;
    top: 6px;
    border: 1px solid #fff;
    border-radius: 58% 42% 55% 45% / 56% 45% 55% 44%;
    transform: rotate(-20deg);
    transition: transform 0.5s cubic-bezier(0.585, 2.5, 0.645, 0.58);
  }
  &:hover::after {
    transform: translate3d(0, -5px, 0);
  }
  @media (max-width: 800px) {
    & {
      transform: scale3d(0.7, 0.7, 0.7);
      font-size: 23px;
    }
  }
}
.coloredIcon {
  color: #fff;
  font-size: 35px;
  &:hover {
    animation: coloredIconAnimation 0.2s infinite;
  }
  @keyframes coloredIconAnimation {
    0% {
      text-shadow: 5px 4px $red-color, -5px -6px $blue-color;
    }
    25% {
      text-shadow: -5px -6px $red-color, 5px 4px $blue-color;
    }
    50% {
      text-shadow: 5px -4px $red-color, -8px 4px $blue-color;
    }
    75% {
      text-shadow: -8px -4px $red-color, -5px -4px $blue-color;
    }
    100% {
      text-shadow: -5px 0px $red-color, 5px -4px $blue-color;
    }
  }
}
.coolCircleEyeButton {
  position: relative;
  display: inline-block;
  .textcircle {
    display: block;
    width: 250px;
    animation: rotate 7s linear infinite;
    text {
      font-size: 32px;
      text-transform: uppercase;
      fill: #ffa600;
      text-transform: uppercase;
        letter-spacing: 2px;
    }
  }
  @keyframes rotate {
    to {
      transform: rotate(-360deg);
    }
  }
  &:hover {
    text {
      font-weight: 500;
    }
  }
  .eye__inner,
  .eye__iris,
  .eye__lashes-up {
    animation: hide 3s step-end infinite;
    @keyframes hide {
      0% {
        opacity: 0;
      }
      10% {
        opacity: 1;
      }
    }
  }
  .eye__lashes-down {
    animation: show 3s step-end infinite;
    @keyframes show {
      0% {
        opacity: 1;
      }
      10% {
        opacity: 0;
      }
    }
  }
}
.eye {
  position: absolute;
  width: 60px;
  height: 60px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  &__outer,
  &__inner,
  &__lashes-down,
  &__lashes-up {
    stroke: #ffa600;
    fill: none;
    stroke-width: 1.5px;
  }
  &__lashes-down {
    opacity: 0;
  }
}
.project__tags {
  position: absolute;
  bottom: 1rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
}
.coolFunnyLink {
  position: relative;
  font-family: sans-serif;
  svg {
    position: absolute;
    bottom: -5px;
    left: 0;
    stroke-width: 2px;
    width: 80px;
    path {
      stroke-dasharray: 1;
      stroke-dashoffset: 1;
      transition: stroke-dashoffset 0.6s cubic-bezier(0.7, 0, 0.3, 1);
    }
  }
  &:hover {
    svg path {
      stroke-dashoffset: 0;
      transition-timing-function: cubic-bezier(0.8, 1, 0.7, 1);
    }
  }
}
